mapper.select:
SELECT 

FROM prho_project_task a

WHERE a.id = #{id}

SELECT 

FROM prho_project_task a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.taskmanager = #{taskmanager}


		AND a.taskname = #{taskname}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM prho_project_task a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE prho_project_task SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
		SELECT 

		FROM prho_project_task a


			a.del_flag = #{DEL_FLAG_NORMAL}

				AND a.project_id = #{projectId}


				AND a.taskname = #{taskname}


				AND a.taskmanager = #{taskmanager}


				AND a.taskcompleteschedule = '100'


				AND a.taskcompleteschedule != '100'


				AND a.tasktype = 'private'


       and DATE_FORMAT(tastplanbegintime, '%Y-%m-%d')>=  DATE_FORMAT(#{starttime}, '%Y-%m-%d')     


      and DATE_FORMAT(taskplanendtime, '%Y-%m-%d') <= DATE_FORMAT(#{endtime}, '%Y-%m-%d')      




				ORDER BY ${page.orderBy}


				ORDER BY a.update_date DESC
