mapper.select:
SELECT 

FROM prho_project_task a

WHERE a.id = #{id}

SELECT 

FROM prho_project_task a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.taskmanager = #{taskmanager}


		AND a.taskname = #{taskname}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM prho_project_task a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE prho_project_task SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT sum(a.expectedhour) AS "expectedhour", a.taskcompletetime AS "taskcompletetime", prinfo.projectName AS "projectName", user2.name AS "staffid" FROM prho_project_task a
a.del_flag = #{DEL_FLAG_NORMAL} and prinfo.del_flag='0' and user2.del_flag='0' and worktype='development'
AND a.project_id = #{projectId}
and DATE_FORMAT(taskcompletetime, '%Y-%m-%d')= DATE_FORMAT(#{daytime}, '%Y-%m-%d')
and DATE_FORMAT(taskcompletetime, '%Y-%m')>= DATE_FORMAT(#{monthstarttime}, '%Y-%m')
and DATE_FORMAT(taskcompletetime, '%Y-%m')<= DATE_FORMAT(#{monthendtime}, '%Y-%m')
AND pup.user_id= #{staffid}
ORDER BY ${page.orderBy}
GROUP BY pup.user_id,a.project_id
